import { useState, useEffect } from 'react';
import { auth } from '../database/dbconfig';
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  onAuthStateChanged,
  signOut,
} from 'firebase/auth';
import Firestore from './firestore';

const useAuth = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const { getUserProfile,setUserProfile } = Firestore();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      if (user) {
        const userProfile = await getUserProfile(user.uid);
        setUser({ ...user, ...userProfile });
      } else {
        setUser(null);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  const signIn = async (email, password) => {
    setLoading(true);
    try {
      await signInWithEmailAndPassword(auth, email, password);
      const userProfile = await getUserProfile(auth.currentUser.uid);
      setUser({ ...auth.currentUser, ...userProfile });
    } catch (error) {
      console.error('Sign In Error:', error.code, error.message);
      throw error;
    } finally {
      setLoading(false);
    }
  };

  const signUp = async (email, password, data) => {
    try {
      const userCredential = await createUserWithEmailAndPassword(
        auth,
        email,
        password
      );
      delete data.password; // Don't store the password in the user profile
      await setUserProfile(userCredential.user.uid, data);
      setUser({ ...userCredential.user, ...data });
    } catch (error) {
      console.error('Sign Up Error:', error.code, error.message);
      throw error;
    }
  };

  const signOutUser = async () => {
    try {
      await signOut(auth);
      setUser(null);
    } catch (error) {
      console.error('Sign Out Error:', error.code, error.message);
      throw error;
    }
  };

  return { user, loading, signIn, signUp, signOutUser };
};

export default useAuth;
